var $ = require('jquery');

$(document).on('click', '.link', function() {

    var link = $(this).attr('data-link');
    var link_blank = $(this).attr('data-link_blank');

    //url in same window
    if (link) {
        window.location = link;
    }

    //url in new window
    if (link_blank) {
        window.open(link_blank, '_blank');
    }

});