
var $ = require('jquery');


module.exports = function(Date_ugly) {

    var Months = [
        'Jan',
        'Feb',
        'Mar',
        'Apr',
        'May',
        'Jun',
        'Jul',
        'Aug',
        'Sep',
        'Oct',
        'Nov',
        'Dec'
    ];


    var Ugly_split = Date_ugly.split('-');
    var Month_ugly = Ugly_split[1];
    var Year_ugly = Ugly_split[0];
    var Day_ugly = Ugly_split[2];


    var Month_pretty = Months[Month_ugly - 1];

    var d = new Date();
    var Year_current = d.getFullYear();


    var Date_pretty = '';
    if(Year_ugly > Year_current){

        Date_pretty = '<span class="Month">' + Month_pretty + '</span> <span class="Day">' + Day_ugly + ',</span> <span class="Year">' + Year_ugly + '</span>';

    } else {

        Date_pretty = '<span class="Month">' + Month_pretty + '</span> <span class="Day">' + Day_ugly + '</span>';
    }

    return Date_pretty;

};